/**
 * Receives two ids, firstId and secId.
 * If firstId exists and is greater than 0, it is returned.
 * If firstId is not present, secId is taken and limited to a maximum of 10 characters.
 * If neither of the two parameters is present, an empty string is returned.
 *
 * @param {string | undefined} firstId - The initial ID.
 * @param {string | undefined} secId - The secondary ID.
 *
 * @returns {string} - The retrieved ID.
 */
export declare function getId(firstId: string | undefined, secId: string | undefined): string;
